import React, { useEffect, useState } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {

    const navigate = useNavigate();

    const [user, setUser] = useState(null);
    const [deeds, setDeeds] = useState([]);
    const [file, setFile] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [formData, setFormData] = useState({
      first_name: "",
      last_name: "",
      email: "",
    })
    // for deeds counter
    const [fulfilledDeeds, setFulfilledDeeds] = useState(0);
    const [unfulfilledDeeds, setUnfulfilledDeeds] = useState(0);
    const [deedsFulfilledForOthers, setDeedsFulfilledForOthers] = useState(0);
    // jwt token 
    const token = localStorage.getItem("token");

    useEffect(() => {
        if (token) {
          const decodedToken = jwtDecode(token);
          fetchUserDetails(decodedToken.user_id);
          fetchDeeds(decodedToken.user_id);
        }
      }, [token]);

    // Fetch user details from the backend
    const fetchUserDetails = async (userId) => {
      try {
        const response = await axios.get(`http://localhost:3000/users/${userId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        setUser(response.data);
        setFulfilledDeeds(response.data.fulfilled_deeds);
        setUnfulfilledDeeds(response.data.unfulfilled_deeds);
        setDeedsFulfilledForOthers(response.data.deeds_fulfilled_for_others);

        setFormData({
          first_name: response.data.first_name,
          last_name: response.data.last_name,
          email: response.data.email,
        });
      } catch (error) {
        console.error("Error fetching user details:", error);
      }
    };

    // Handle input changes
    const handleChange = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // upload user id document/profile picture
    const handleFileChange = (event) => {
      setFile(event.target.files[0]);
    };

    // Handle updating user details and file upload
    const handleUpdate = async () => {
      try {
        const updateData = new FormData();
        updateData.append("user[first_name]", formData.first_name);
        updateData.append("user[last_name]", formData.last_name);
        updateData.append("user[email]", formData.email);

        if (file) {
          updateData.append("user[id_document]", file);
        }

        await axios.put(`http://localhost:3000/users/${user.id}`, updateData, {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        });

        // alert("User details updated successfully!");
        setIsEditing(false);
        fetchUserDetails(user.id);

        // ✅ Refresh the page after successful update
        setTimeout(() => {
          window.location.reload();
        }, 100); // Slight delay to ensure state updates before refresh

      } catch (error) {
        console.error("Error updating user details:", error);
        alert("Failed to update user details.");
      }
    };

    // Fetch deeds requested or volunteered by the user
    const fetchDeeds = async (userId) => {
      try {
        const response = await axios.get(`http://localhost:3000/users/${userId}/deeds`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setDeeds(response.data);
      } catch (error) {
        console.error("Error fetching deeds:", error);
      }
    };

    const [isAddingDeed, setIsAddingDeed] = useState(false);
    const [deedData, setDeedData] = useState({
      description: "",
      deed_type: "",
      address: "",
      latitude: "",
      longitude: ""
    });

    const handleDeedChange = (e) => {
      setDeedData({ ...deedData, [e.target.name]: e.target.value });
    };

    // Convert Address to Latitude & Longitude
    const getCoordinatesFromAddress = async (address) => {
      const apiKey = "AIzaSyAx_Rbj5JBqB_QStMi27jDFWMf3HJ-aZm0"; // Replace with your API key
      const url = `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(address)}&key=${apiKey}`;

      try {
        const response = await axios.get(url);
        if (response.data.results.length > 0) {
          const location = response.data.results[0].geometry.location;
          setDeedData ((prevData) => ({
            ...prevData,
            latitude: location.lat,
            longitude: location.lng,
          }));
        } else {
          alert("Address not found. Please enter a valid address.");
        }
      } catch (error) {
        console.error("Error fetching coordinates:", error);
        alert("Could not fetch location. Try again.");
      }
    };

    // Handle submitting a new deed request
    const handleSubmitDeed = async () => {
      if (!deedData.description || !deedData.deed_type) {
        alert("Please fill all fields");
        return;
      }

      // Ensure latitude and longitude are set
      if (!deedData.latitude || !deedData.longitude) {
        alert("Please click 'Convert Address to Coordinates' first.");
        return;
      }

      try {
        await axios.post(
          "http://localhost:3000/deeds",
          {
            deed: {
              description: deedData.description,
              deed_type: deedData.deed_type,
              latitude: deedData.latitude,
              longitude: deedData.longitude,
              address: deedData.address, // Include address
            },
          },
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        setIsAddingDeed(false);
        fetchDeeds(user.id);
        setTimeout(() => {
          window.location.reload();
        }, 100);
      } catch (error) {
        console.error("Error submitting deed request:", error);
        alert("Failed to submit deed request.");
      }
    };

    const handleDeleteDeed = async (deedId) => {
      if (!window.confirm("Are you sure you want to delete this deed?")) return;

      try {
        await axios.delete(`http://localhost:3000/users/${user.id}/deeds/${deedId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        alert("Deed deleted successfully!");
        fetchDeeds(user.id);
      } catch (error) {
        console.error("Error deleting deed:", error.response?.data || error.message);
        alert("Failed to delete deed.");
      }
    };

    // handle the logout event
    const handleLogout = () => {
        localStorage.removeItem("token");
        window.location.reload();
    };

    return (
        <div>
          {user ? (
            <>
              <h2>Welcome, {user.first_name} {user.last_name}</h2>
              {/* <p>Email: {user.email}</p> */}

              {isEditing ? (
                <div>
                  <h3>Edit Profile</h3>
                  <input type="text" name="first_name" value={formData.first_name} onChange={handleChange} placeholder="First Name" />
                  <input type="text" name="last_name" value={formData.last_name} onChange={handleChange} placeholder="Last Name" />
                  <input type="email" name="email" value={formData.email} onChange={handleChange} placeholder="Email" />

                  <h4>Upload New ID Document (optional)</h4>
                  <input type="file" accept="image/png, image/jpeg, application/pdf" onChange={handleFileChange} />

                  <button onClick={handleUpdate}>Save Changes</button>
                  <button onClick={() => setIsEditing(false)}>Cancel</button>
                </div>
              ) : (
                <button onClick={() => setIsEditing(true)}>Update Details</button>
              )}

              {user.id_document_url ? (
                <div>
                  {user.id_document_url.endsWith(".pdf") ? (
                    <a href={user.id_document_url} target="_blank" rel="noopener noreferrer">
                      View PDF Document
                    </a>
                  ) : (
                    <img src={user.id_document_url} alt="Uploaded ID Document" style={{ width: "200px", height: "auto" }} />
                  )}
                </div>
              ) : (
                <p>No ID document uploaded.</p>
              )}

              {/* <h3>Upload ID Document</h3>
              <input type="file" accept="image/png, image/jpeg, application/pdf" onChange={handleFileChange} /> */}
              {/* <button onClick={handleUpdate}>Upload</button> */}
            </>
          ) : (
            <p>Loading user details...</p>
          )}

          <button onClick={() => setIsAddingDeed(!isAddingDeed)}>
            {isAddingDeed ? "Cancel" : "Request a Deed"}
          </button>

          {isAddingDeed && (
            <div>
              <h3>Request a Deed</h3>
              <input
                type="text"
                name="description"
                placeholder="Description"
                value={deedData.description}
                onChange={handleDeedChange}
              />
              <select name="deed_type" value={deedData.deed_type} onChange={handleDeedChange}>
                <option value="one-time">One-Time</option>
                <option value="material">Material</option>
              </select>
              <input
                type="text"
                name="address"
                placeholder="Enter Address"
                value={deedData.address}
                onChange={handleDeedChange}
              />
              <button onClick={() => getCoordinatesFromAddress(deedData.address)}>
                Convert Address to Coordinates
              </button>

              <input
                type="text"
                name="latitude"
                value={deedData.latitude}
                placeholder="Latitude"
                readOnly
              />
              <input
                type="text"
                name="longitude"
                value={deedData.longitude}
                placeholder="Longitude"
                readOnly
              />

              <button onClick={handleSubmitDeed} disabled={!deedData.latitude || !deedData.longitude} >Submit Deed</button>
            </div>
          )}

          <h2>Your Deeds</h2>
            <ul>
              {deeds.map((deed) => (
              <li key={deed.id}>
                  {deed.description} - {deed.deed_type} - <strong>{deed.status}</strong>
                  <p><strong>Address:</strong> {deed.address}</p>
                  <button onClick={() => handleDeleteDeed(deed.id)} style={{ marginLeft: "10px", color: "red" }}>
                    Delete
                  </button>
              </li>
              ))}
            </ul>

            <h2>Your Deed Statistics</h2>
            <p>Unfulfilled Deeds: <strong>{unfulfilledDeeds}</strong></p>
            <p>Fulfilled Deeds: <strong>{fulfilledDeeds}</strong></p>
            <p>Deeds Fulfilled for Others: <strong>{deedsFulfilledForOthers}</strong></p>

            <button onClick={() => navigate("/maps")}>View Deeds on Map</button>

          <button onClick={handleLogout}>Logout</button>
        </div>
    );
};

export default Dashboard;
