import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Navbar = () => {

    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const token = localStorage.getItem("token");

    useEffect(() => {
    // Check if the user is logged in (example: from localStorage or JWT)
    const token = localStorage.getItem("token"); // Assume token is stored after login
    setIsLoggedIn(!!token); // Converts token existence to true/false
    }, []);

    // handle the logout event
    const handleLogout = () => {
        localStorage.removeItem("token");
        window.location.reload();
    };

    return (
    <nav>
        <ul>
        <li>
            <Link to="/">Home</Link>
        </li>
        <li>
            <Link to="/dashboard">Dashboard</Link>
        </li>
        <li>
            <Link to="/maps">Map</Link>
        </li>
        <li >
            {isLoggedIn ? (
                <Link onClick={handleLogout} to="/logout">Logout</Link>
            ) : (
                <Link to="/login">Login</Link>
            )}
        </li>
        </ul>
    </nav>
    );
};

export default Navbar;
