import React, { useEffect, useState } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode";

const Dashboard = () => {
    const [user, setUser] = useState(null);
    const [deeds, setDeeds] = useState([]);
    const [file, setFile] = useState(null);
    const token = localStorage.getItem("token");

    useEffect(() => {
        if (token) {
          const decodedToken = jwtDecode(token);
          fetchUserDetails(decodedToken.user_id);
          fetchDeeds(decodedToken.user_id);
        }
      }, [token]);

    // Fetch user details from the backend
    const fetchUserDetails = async (userId) => {
      try {
        const response = await axios.get(`http://localhost:3000/users/${userId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(response.data);
      } catch (error) {
        console.error("Error fetching user details:", error);
      }
    };

    // Fetch deeds requested or volunteered by the user
    const fetchDeeds = async (userId) => {
      try {
        const response = await axios.get(`http://localhost:3000/users/${userId}/deeds`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setDeeds(response.data);
      } catch (error) {
        console.error("Error fetching deeds:", error);
      }
    };

    // upload user id document/profile picture
    const handleFileChange = (event) => {
      setFile(event.target.files[0]);
    };

    const handleUpload = async () => {
      if (!file) {
        alert("Please select a file to upload.");
        return;
      }

      const formData = new FormData();
      formData.append("user[id_document]", file);

      try {
        await axios.put(`http://localhost:3000/users/${user.id}`, formData, {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        });

        alert("ID Document uploaded successfully!");
        fetchUserDetails(user.id);
      } catch (error) {
        console.error("Error uploading file:", error);
        alert("Upload failed.");
      }
    };

    // handle the logout event
    const handleLogout = () => {
        localStorage.removeItem("token");
        window.location.reload();
    };



    return (
        <div>
          {user ? (
            <>
              <h2>Welcome, {user.first_name} {user.last_name}</h2>
              <p>Email: {user.email}</p>

              {user.id_document_url ? (
            <div>
              <h3>Your Uploaded ID Document</h3>
              {user.id_document_url.endsWith(".pdf") ? (
                <a href={user.id_document_url} target="_blank" rel="noopener noreferrer">
                  View PDF Document
                </a>
              ) : (
                <img src={user.id_document_url} alt="Uploaded ID Document" style={{ width: "200px", height: "auto" }} />
              )}
            </div>
          ) : (
            <p>No ID document uploaded.</p>
          )}

              <h3>Upload ID Document</h3>
              <input type="file" accept="image/png, image/jpeg, application/pdf" onChange={handleFileChange} />
              <button onClick={handleUpload}>Upload</button>
            </>
          ) : (
            <p>Loading user details...</p>
          )}
          <h2>Your Deeds</h2>
          <h3></h3>
          <ul>
              {deeds.map((deed) => (
              <li key={deed.id}>
                  {deed.description} - <strong>{deed.status}</strong>
              </li>
              ))}
          </ul>
          <button onClick={handleLogout}>Logout</button>
        </div>
    );
};

export default Dashboard;
