import React, { useEffect, useState } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode";
import { createConsumer } from "@rails/actioncable";
import Navbar from "../components/NavBar";
import { useNavigate } from "react-router-dom";



import "./ChatPage.css"; // Import styles

const ChatPage = () => {
  const navigate = useNavigate();
  const [deeds, setDeeds] = useState([]);

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (token) {
      const decodedToken = jwtDecode(token);
      fetchUserDetails(decodedToken.user_id);
      fetchDeeds(decodedToken.user_id);
    }

    // Set up ActionCable to listen for deed updates
  const cable = createConsumer("ws://localhost:3000/cable");

  const deedsChannel = cable.subscriptions.create("DeedsChannel", {
    received: (data) => {
      console.log("Received deed update:", data);
      setDeeds(data.deeds);
    },
  });

  return () => {
    deedsChannel.unsubscribe();
  };

}, [token]);

  // fetch user details from api
  const fetchUserDetails = async (userId) => {

    if (!userId) return;

    try {
      const response = await axios.get(`http://localhost:3000/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setUser(response.data);
      setUnfulfilledDeeds(response.data.unfulfilled_deeds);

      // Fetch volunteered deeds
      const volunteeredResponse = await axios.get(`http://localhost:3000/users/${userId}/volunteered_deeds`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      const allVolunteeredDeeds = volunteeredResponse.data;
      // Filter only deeds where the user was the one who completed them

      setVolunteeredDeeds(allVolunteeredDeeds);
      } catch (error) {
        console.error("Error fetching user details:", error);
      }
    };


  // Fetch deeds requested or volunteered by the user
  const fetchDeeds = async (userId) => {
    try {
      const response = await axios.get(`http://localhost:3000/users/${userId}/deeds`, {
        headers: { Authorization: `Bearer ${token}` },
      });


      setDeeds(response.data);
      const volunteeredResponse = await axios.get(`http://localhost:3000/users/${userId}/volunteered_deeds`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setVolunteeredDeeds(volunteeredResponse.data);
    } catch (error) {
      console.error("Error fetching deeds:", error);
    }
  };

  return (
    <div className="chat-container">
      {/* Left Column - List of Volunteers */}
      <div className="volunteer-column">
        <h2>My Volunteers</h2>
        {deeds.length > 0 ? (
          deeds.map((deed) => (
            <div key={deed.id} className="deed-card">
              <h3>{deed.description}</h3>
              {/* List of Volunteers */}
              {deed.volunteers.length > 0 ? (
                <div className="volunteer-list">
                  {deed.volunteers.map((volunteer) => (
                    <div key={volunteer.id} className="volunteer-item">
                      <p>{volunteer.first_name} {volunteer.last_name}</p>
                      <button className="chat-button">Chat</button>
                    </div>
                  ))}
                </div>
              ) : (
                <p>No volunteers yet.</p>
              )}
            </div>
          ))
        ) : (
          <p>No deeds found.</p>
        )}
      </div>

      {/* Right Column - Chat Messages */}
      <div className="chat-column">
        <h2>Chat Window</h2>
        <div className="chat-box">
          <p>Incoming and outgoing messages will appear here.</p>
        </div>
      </div>
    </div>
  );
}

export default ChatPage;
