import React, { useEffect, useState } from "react";
import axios from "axios";
import { GoogleMap, Marker, InfoWindow } from "@react-google-maps/api";
import { LoadScriptNext } from "@react-google-maps/api";

const mapContainerStyle = { width: "100%", height: "100vh" };
// chamge center of map to be current logged in users lat and lon
const center = { lat: -25.7479, lng: 28.2293 }; // Default center (Pretoria, South Africa)

const MapsPage = () => {
  const [deeds, setDeeds] = useState([]);
  const [selectedDeed, setSelectedDeed] = useState(null);
  const apiKey = "AIzaSyAx_Rbj5JBqB_QStMi27jDFWMf3HJ-aZm0"; // Replace with your actual API key

  useEffect(() => {
    fetchDeeds();
  }, []);

  // Fetch all unfulfilled deeds with lat/lon
  const fetchDeeds = async () => {
    try {
      const response = await axios.get("http://localhost:3000/deeds");
      setDeeds(response.data);
    } catch (error) {
      console.error("Error fetching deeds:", error);
    }
  };

   // Function to format date
   const formatDate = (dateString) => {
    if (!dateString) return "N/A";
    const date = new Date(dateString);
    return date.toLocaleDateString("en-US", {
      weekday: "long", // Example: Monday
      year: "numeric",
      month: "long", // Example: January
      day: "numeric", // Example: 5
    });
  };

  return (
    <LoadScriptNext googleMapsApiKey={apiKey}>
      <GoogleMap mapContainerStyle={mapContainerStyle} zoom={10} center={center}>
        {deeds.map((deed) => (
          <Marker
            key={deed.id}
            position={{ lat: parseFloat(deed.latitude), lng: parseFloat(deed.longitude) }}
            onClick={() => setSelectedDeed(deed)}
          />
        ))}

        {selectedDeed && (
          <InfoWindow
            position={{ lat: parseFloat(selectedDeed.latitude), lng: parseFloat(selectedDeed.longitude) }}
            onCloseClick={() => setSelectedDeed(null)}
          >
            <div>
              <h3>{selectedDeed.description}</h3>
              <p><strong>Type:</strong> {selectedDeed.deed_type}</p>
              <p><strong>Volunteers:</strong> {selectedDeed.volunteer_count}</p>
              <p><strong>Status:</strong> {selectedDeed.status}</p>
              <p><strong>Created On:</strong> {formatDate(selectedDeed.created_at)}</p>
            </div>
          </InfoWindow>
        )}
      </GoogleMap>
    </LoadScriptNext>
  );
};

export default MapsPage;
