import React, { useEffect, useState } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode";
// import { createConsumer } from "@rails/actioncable";
import { useParams } from "react-router-dom";
import "./ChatPage.css"; // Import styles

const ChatPage = () => {
  const { requesterId, volunteerId } = useParams(); // Get IDs from the URL
  const [user, setUser] = useState(null);
  const [volunteers, setVolunteers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  // const [messages, setMessages] = useState([]);
  // const [newMessage, setNewMessage] = useState("");
  const token = localStorage.getItem("token");
  const navigate = useNavigate();

  useEffect(() => {
    if (token) {
      const decodedToken = jwtDecode(token);
      fetchUserDetails(decodedToken.user_id);
      fetchDeeds(decodedToken.user_id);
    }
  }, [token, selectedUser]);

  // Fetch the logged-in user's details
  const fetchUserDetails = async (userId) => {
    if (!userId) return;
    try {
      const response = await axios.get(`http://localhost:3000/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUser(response.data);
    } catch (error) {
      console.error("Error fetching user details:", error);
    }
  };

  // Fetch the volunteers for the logged-in user's deed requests
  const fetchVolunteers = async (userId) => {
    try {
      const response = await axios.get(`http://localhost:3000/users/${userId}/volunteers`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setVolunteers(response.data);
    } catch (error) {
      console.error("Error fetching volunteers:", error);
    }
  };

  // Setup WebSocket for real-time chat
  // const setupChatWebSocket = () => {
  //   const cable = createConsumer("ws://localhost:3000/cable");
