  //     { channel: "ChatRoomChannel", requester_id: requesterId, volunteer_id: volunteerId },
  //     {
  //       received: (data) => {
  //         setMessages((prevMessages) => [...prevMessages, data.message]);
  //       },
  //     }
  //   );
  // };

  // Fetch chat messages between requester and volunteer
  // const fetchMessages = async (otherUserId) => {
  //   try {
  //     const response = await axios.get(`http://localhost:3000/chat_rooms/${requesterId}/${otherUserId}`, {
  //       headers: { Authorization: `Bearer ${token}` },
  //     });
  //     setMessages(response.data.messages);
  //     setSelectedUser(otherUserId);
  //   } catch (error) {
  //     console.error("Error fetching messages:", error);
  //   }
  // };

  // Send a new message
  // const sendMessage = async () => {
  //   if (!newMessage.trim()) return;

  //   try {
  //     const response = await axios.post(
  //       `http://localhost:3000/chat_rooms/${requesterId}/${selectedUser}/messages`,
