  //     );

  //     setMessages((prevMessages) => [...prevMessages, response.data]);
  //     setNewMessage("");
  //   } catch (error) {
  //     console.error("Error sending message:", error);
  //   }
  // };

  return (
    <div className="chat-container">
      {/* Left Column - User Info & Volunteers */}
      <div className="left-column">
        {user && (
          <div className="user-info">
            <h2>{user.first_name} {user.last_name}</h2>
            <p>Email: {user.email}</p>
          </div>
        )}
        <h3>Volunteers for Your Deeds</h3>
        <ul className="volunteer-list">
          {volunteers.map((volunteer) => (
            <li key={volunteer.id} className="volunteer-item">
              {volunteer.first_name} {volunteer.last_name}
            </li>
          ))}
        </ul>
      </div>

      {/* Right Column - Chat Messages */}
      <div className="right-column">
        {selectedUser ? (
          <>
            <h3>Chat with {volunteers.find((v) => v.id === selectedUser)?.first_name}</h3>
            <div className="messages-container">
              {/* {messages.map((msg, index) => (
                <div key={index} className={`message ${msg.sender_id === user.id ? "sent" : "received"}`}>
                  <p>{msg.content}</p>
                </div>
              ))} */}
            </div>
            <div className="message-input">
              <input
                type="text"
                placeholder="Type a message..."
                value=""
                // onChange={(e) => setNewMessage(e.target.value)}
              />
              <button> Send</button>
            </div>
          </>
        ) : (
          <p>Select a volunteer to start chatting.</p>
        )}
      </div>
    </div>
  );
};

export default ChatPage;
