import React, { useEffect, useState } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode";
import { useNavigate } from "react-router-dom";

// Import components & styles
// import Navbar from "../components/NavBar";
import "./Dashboard.css"; // Import new CSS file

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [deeds, setDeeds] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [isAddingDeed, setIsAddingDeed] = useState(false);

  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    email: "",
  });

  const [deedData, setDeedData] = useState({
    description: "",
    deed_type: "one-time",
    address: "",
    latitude: "",
    longitude: "",
  });

  // User statistics
  const [fulfilledDeeds, setFulfilledDeeds] = useState(0);
  const [unfulfilledDeeds, setUnfulfilledDeeds] = useState(0);
  const [deedsFulfilledForOthers, setDeedsFulfilledForOthers] = useState(0);

  // JWT token
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (token) {
      const decodedToken = jwtDecode(token);
      fetchUserDetails(decodedToken.user_id);
      fetchDeeds(decodedToken.user_id);
    }
  }, [token]);

  // Fetch user details
  const fetchUserDetails = async (userId) => {
    try {
      const response = await axios.get(`http://localhost:3000/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setUser(response.data);
      setFulfilledDeeds(response.data.fulfilled_deeds);
      setUnfulfilledDeeds(response.data.unfulfilled_deeds);
      setDeedsFulfilledForOthers(response.data.deeds_fulfilled_for_others);

      setFormData({
        first_name: response.data.first_name,
        last_name: response.data.last_name,
        email: response.data.email,
      });
    } catch (error) {
      console.error("Error fetching user details:", error);
    }
  };

  // Fetch deeds requested by the user
  const fetchDeeds = async (userId) => {
    try {
      const response = await axios.get(`http://localhost:3000/users/${userId}/deeds`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setDeeds(response.data);
    } catch (error) {
      console.error("Error fetching deeds:", error);
    }
  };

  // Handle user logout
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="dashboard-container">
      <Navbar />

      <div className="dashboard-content">
        {user ? (
          <>
            <div className="profile-card">
              <h2>Welcome, {user.first_name} {user.last_name}</h2>
              <p>Email: {user.email}</p>
              <button className="action-button" onClick={() => setIsEditing(!isEditing)}>
                {isEditing ? "Cancel" : "Edit Profile"}
              </button>
            </div>

            {isEditing && (
              <div className="edit-profile-card">
                <input type="text" name="first_name" value={formData.first_name} placeholder="First Name" />
                <input type="text" name="last_name" value={formData.last_name} placeholder="Last Name" />
                <input type="email" name="email" value={formData.email} placeholder="Email" />
                <button className="action-button">Save Changes</button>
              </div>
            )}

            <div className="deeds-container">
              <div className="deeds-card">
                <h3>Your Deeds</h3>
                <button className="action-button" onClick={() => setIsAddingDeed(!isAddingDeed)}>
                  {isAddingDeed ? "Cancel" : "Request a Deed"}
                </button>

                {isAddingDeed && (
                  <div className="add-deed-card">
                    <input type="text" name="description" placeholder="Description" />
                    <select name="deed_type">
                      <option value="one-time">One-Time</option>
                      <option value="material">Material</option>
                    </select>
                    <input type="text" name="address" placeholder="Enter Address" />
                    <button className="action-button">Submit Deed</button>
                  </div>
                )}

                <ul>
                  {deeds.map((deed) => (
                    <li key={deed.id}>
                      {deed.description} - {deed.deed_type} - <strong>{deed.status}</strong>
                      <button className="delete-button">Delete</button>
                    </li>
                  ))}
                </ul>
              </div>

              <div className="stats-card">
                <h3>Your Deed Statistics</h3>
                <p>Unfulfilled Deeds: <strong>{unfulfilledDeeds}</strong></p>
                <p>Fulfilled Deeds: <strong>{fulfilledDeeds}</strong></p>
                <p>Deeds Fulfilled for Others: <strong>{deedsFulfilledForOthers}</strong></p>
                <button className="action-button" onClick={() => navigate("/maps")}>
                  View Deeds on Map
                </button>
              </div>
            </div>

            <button className="logout-button" onClick={handleLogout}>Logout</button>
          </>
        ) : (
          <p>Loading user details...</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
